mapper.select:
SELECT CONCAT_WS('.',TABLE_SCHEMA, TABLE_NAME) TNAME FROM INFORMATION_SCHEMA.`TABLES` T
TABLE_SCHEMA LIKE 'DD_%' AND lower(TABLE_NAME) >= #{beginTableName} AND lower(TABLE_NAME) <= #{endTableName}
ORDER BY TNAME

select sum(g.cnt) from (

    SELECT count(1) cnt
    FROM ${item.gpsTable}
    WHERE DEVICE_NUM=#{item.deviceNum}
    AND GPS_TIME BETWEEN #{item.beginTime} AND #{item.endTime}
    AND AV='A'

) g

select * from (
SELECT GPS_TIME AS gpsTime, LNG_BD AS lngBd, LAT_BD AS latBd, LNG_GC AS lngGc, LAT_GC AS latGc, DIRECTION_NAME AS directionName, STATUS, CREATE_TIME AS createTime, IS_RESEND AS isOld, ORI_MILEAGE AS oriMileage, TOTAL_MILEAGE AS totalMileage, OBD_SPEED AS obdSpeed, OBD_MESSAGE AS obdMessage, SPEED FROM ${item.gpsTable} WHERE DEVICE_NUM=#{item.deviceNum} AND GPS_TIME BETWEEN #{item.beginTime} AND #{item.endTime} AND AV='A'
) g order by g.gpsTime ASC

select * from (

    SELECT
    GPS_TIME AS gpsTime,
    CREATE_TIME AS createTime,
    ${coordinate}
    ORI_MILEAGE AS oriMileage,
    ACC,
    CASE
        WHEN OBD_SPEED IS NOT NULL and OBD_SPEED!='' THEN OBD_SPEED ELSE SPEED
        END AS speed
    FROM ${item.gpsTable}
    WHERE DEVICE_NUM=#{item.deviceNum}
    AND GPS_TIME BETWEEN #{item.beginTime} AND #{item.endTime}
    AND AV='A'

) g order by g.gpsTime ASC
